import React, { useState, useEffect } from 'react';
import { MDBListGroup, MDBListGroupItem, MDBInput, MDBBtn } from 'mdb-react-ui-kit';
import './Chat.css';

export default function App() {

  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [username, setUsername] = useState("");
  const [userColor, setUserColor] = useState(null);

  // random username, pridjev + imenica npr. Whisperwind
  const randomName = () => {
      const adjectives = ["autumn", "hidden"];
      const nouns = ["waterfall", "river"];
      const adjective = adjectives[Math.floor(Math.random() * adjectives.length)];
      const noun = nouns[Math.floor(Math.random() * nouns.length)];
      const user = `${adjective.slice(0, 1).toUpperCase()}${adjective.slice(1)}${noun}`
      return user;
      }

  // random hex string za boju, npr #FFFFFF
  const randomColor = () => {
    return '#' + Math.floor(Math.random() * 0xFFFFFF).toString(16);
  }

  // pokreni Scaledrone
  const CLIENT_ID = "2sxe3EkxMn0uTqkz";
  const drone = new window.Scaledrone(CLIENT_ID, {
    data: username
  });

  useEffect(() => {

    // dodijeli random korisničko ime i boju kad se stranica renderira
    drone.on("open", error => {
      if (error) {
        alert("Došlo je do pogreške, molim vas pokušajte ponovo!");
        console.error(error);
      }
      setUsername(randomName());
      setUserColor(randomColor());
    });

    // alert za prekid veze
    drone.on("close", (event) => {
      alert("Niste spojeni na Scaledrone;", event);
      console.log("Connection was closed");
    });

    // error handler
    drone.on('error', (error) => {
      alert("Nije uspjelo slanje poruke, molim vas pokušajte ponovo!")
      console.error('Error publishing message:', error);
    })

    return () => {
      // zatvori  kad se komponenta unmounta
      drone.close();
    }
  }, []);

  useEffect(() => {

    // prijavi se na sobu
    const room = drone.subscribe("observable-room");

    // ulaz u sobu
    room.on("open", (error) => {
      if (error) {
        alert("Došlo je do pogreške, molim vas pokušajte ponovo!");
        console.error(error);
        return;
      }
      console.log("Successfully joined room");
    });

    // osluškivanje novih poruka
    room.on("data", (message) => {

      const newMessage = {
        username: message.member,
        text: message.data
      };

      setMessages(prevMessages => [...prevMessages, newMessage]);
    });

    return () => {
      // odjavi se iz sobe kad se komponenta unmounta
      room.unsubscribe();
    }
  }, [])


const handleMessageChange = (event) => {
  setMessage(event.target.value);
};

const handleMessageSubmit = (event) => {

  event.preventDefault();

  const newMessage = {
    username: username,
    text: message
  };

  setMessages(messages => [...messages, newMessage]);
  setMessage("");

  drone.publish({
    room: 'observable-room',
    message: {
      username: username,
      text: message
    },
    clientData: {
      username: username,
      text: message
    }
  });
};

const renderMessages = () => {
  return messages.map((message, index) => {
    const messageClass = message.username === username ? 'my-message' : 'other-message';
    return (
      <MDBListGroupItem
        key={index}
        className={`mb-2 m-0 text-white d-flex justify-content-${message.username === username ? "end" : "start"}`}
        style={{ backgroundColor: userColor}}
      >
        <div className={`d-flex flex-column justify-content-${message.username === username ? "end" : "start"}`}>
          <span className={`font-weight-bold text-black ${messageClass}`}>{message.username}:</span>
          <span className={`text-${message.username === username ? "end" : "start"}`}>{message.text}</span>
        </div>
      </MDBListGroupItem>
    );
  });
};

  return (
    <div className="container p-3 d-flex-column align-items-center">
      <div className="chat-header d-flex align-items-center justify-content-center">
        <h2 className="my-5 text-black">Moj Chat App</h2>
      </div>
      <div className="chat-body mb-3 p-5 border border-info border-3">
        <MDBListGroup className="mb-3">{renderMessages()}</MDBListGroup>
        <form onSubmit={handleMessageSubmit}>
          <div className="text-center">
            <MDBInput className="mb-3 border border-3 border-info" type="text" placeholder="Napišite poruku..." value={message} onChange={handleMessageChange} autoFocus="true"/>
            <MDBBtn color="primary" type="submit">Pošalji</MDBBtn>
          </div>
        </form>
      </div>
      <div className="chat-footer text-center text-black">
        <p>Vaše trenutno korisničko ime je: {username}</p>
      </div>
    </div>
  );
};
