import React, { useState, useEffect } from 'react';
import { MDBListGroup, MDBListGroupItem, MDBInput, MDBBtn } from 'mdb-react-ui-kit';
import './Chat.css';

export default function App() {

  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [username, setUsername] = useState("");
  const [userColor, setUserColor] = useState("#");

  const randomName = () => {
      const adjectives = ["autumn", "hidden"];
      const nouns = ["waterfall", "river"];
      const adjective = adjectives[Math.floor(Math.random() * adjectives.length)];
      const noun = nouns[Math.floor(Math.random() * nouns.length)];
      const user = `${adjective.slice(0, 1).toUpperCase()}${adjective.slice(1)}${noun}`
      return user;
      }

  const randomColor = () => {
    return '#' + Math.floor(Math.random() * 0xFFFFFF).toString(16);
  }

  useEffect(() => {
    setUsername(randomName());
    setUserColor(randomColor());
  }, []);

  const CLIENT_ID = "xZBf1Qt6e8zvkd3X";
    const drone = new window.Scaledrone(CLIENT_ID, {
      data: {
          name: randomName()
        },
      }
    );

  useEffect(() => {

    drone.on("open", error => {
      if (error) {
          alert("Došlo je do pogreške, molim vas pokušajte ponovo!");
          console.error(error);
        }
    });

    const room = drone.subscribe("observable-room");

    room.on("open", (error) => {
      if (error) {
        alert("Došlo je do pogreške, molim vas pokušajte ponovo!");
        console.error(error);
        return;
      }
      console.log("Successfully joined room");
    });

    drone.on("close", (event) => {
      console.log("Connection was closed", event);
    });

    }, []);

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleMessagesSubmit = (event) => {
    event.preventDefault();

    const newMessage = {
      username: username,
      text: message
    };

    setMessages(messages => [...messages, newMessage]);
    setMessage("");

    drone.publish(
      {
        room: 'observable-room',
        message: newMessage,
        clientData: { 
          username: username
        }
      }
    ).then(() => {
      console.log("Message published successfully!");
    }).catch(error => {
      console.error("Error publishing message:", error);
    });
  };

  const renderMessages = () => {
    return messages.map((message, index) => {
      const messageClass = message.username === username ? 'user-message' : 'other-message';
      return (
        <MDBListGroupItem key={index} className={`d-flex justify-content-${message.username === username ? "end" : "start"}`} style={{backgroundColor: userColor()}}>
          <span className={`font-weight-bold ${messageClass}`}>{message.username}</span>: {message.text}
        </MDBListGroupItem>
      );
    });
  };

  return (
    <div className="container p-5 d-flex-column align-items-center">
      <div className="chat-header d-flex align-items-center justify-content-center">
        <h2 className="mb-5">Moj Chat App</h2>
      </div>
      <div className="chat-body mb-3 text-center">
        <MDBListGroup className="mb-3 w-50">{renderMessages()}</MDBListGroup>
        <form onSubmit={handleMessagesSubmit}>
          <MDBInput className="mb-1" type="text" value={message} onChange={handleMessageChange} placeholder="Napišite poruku"/>
          <MDBBtn color="primary" type="submit">Pošalji</MDBBtn>
        </form>
      </div>
      <div className="chat-footer text-center">
        <p>Vaše trenutno korisničko ime je: {username}</p>
      </div>
    </div>
  );
};
