import React, { useState, useEffect, useMemo } from 'react';
import { MDBListGroup, MDBListGroupItem, MDBInput, MDBBtn } from 'mdb-react-ui-kit';
import './Chat.css';

export default function App() {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [username, setUsername] = useState("");
  const [members, setMembers] = useState([]);
  const [userColor, setUserColor] = useState(null);

  const randomName = () => {
    const adjectives = ["autumn", "hidden", "bitter"];
    const nouns = ["waterfall", "river", "breeze"];
    const adjective = adjectives[Math.floor(Math.random() * adjectives.length)];
    const noun = nouns[Math.floor(Math.random() * nouns.length)];
    const user = `${adjective.slice(0, 1).toUpperCase()}${adjective.slice(1)}${noun}`;
    return user;
  }

  const randomColor = () => {
    return '#' + Math.floor(Math.random() * 0xFFFFFF).toString(16);
  }

  const getInverseColor = (hexColor) => {
    const color = hexColor.slice(1);
    const r = 255 - parseInt(color.slice(0, 2), 16);
    const g = 255 - parseInt(color.slice(2, 4), 16);
    const b = 255 - parseInt(color.slice(4, 6), 16);
    return `#${r.toString(16).padStart(2, "0")}${g.toString(16).padStart(2, "0")}${b.toString(16).padStart(2, "0")}`;
  };

  useEffect(() => {
    setUsername(randomName());
    setUserColor(randomColor());
  }, []);

  const CLIENT_ID = "2sxe3EkxMn0uTqkz";
  const drone = useMemo(() => new window.Scaledrone(CLIENT_ID, {
    data: {
      name: username
    },
  }), [username]);

  const handleMembers = (members) => {
    return setMembers(members);
  };

  useEffect(() => {
    drone.on("open", error => {

      if (error) {
          alert("Došlo je do pogreške, molim vas pokušajte ponovo!");
          console.error(error);
        }
    });

    const room = drone.subscribe("observable-room");

    room.on("open", (error) => {

      if (error) {
        alert("Došlo je do pogreške, molim vas pokušajte ponovo!");
        console.error(error);
        return;
      }
      console.log("Successfully joined room");
    });

    room.on("members", (members) => {
      handleMembers(members);
    });

    room.on("data", (message) => {
      if (message.member !== username) {
        const newMessage = {
          username: message.member,
          text: message.data
        };
        setMessages(prevMessages => [...prevMessages, newMessage]);
      }
    });

    room.on('message', message => {
      const {data, id, timestamp, clientId, member} = message;
    });

    drone.on("close", (event) => {
      alert("Connection was closed", event);
      console.log("Connection was closed", event);
    });

  }, [drone, username]);

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleMessagesSubmit = (event) => {

    event.preventDefault();

    const newMessage = {
      username: username,
      text: message
    };

    setMessages(messages => [...messages, newMessage]);
    setMessage('');

    drone.publish({
      room: 'observable-room',
      message: newMessage
    });

    drone.on('error', (error) => {
      alert("Nije uspjelo slanje poruke, molim vas pokušajte ponovo!")
      console.error('Error publishing message:', error);
    });
  };

  const renderMessages = () => {
    return ( messages.map((message, index) => {
      const messageClass = message.username === username ? 'user-message' : 'other-message';
      const textColor = getInverseColor(userColor);
      return (
        <MDBListGroupItem
          key={index}
          className={`message ${messageClass} text-${message.username === username ? "end" : "start"} mb-2 text-white d-flex justify-content-${message.username === username ? "end" : "start"}`}
          style={{ backgroundColor: userColor }}
          >
        <div className={`d-flex flex-column justify-content-${message.username === username ? "end" : "start"}`}>
          <span className={`font-weight-bold text-black ${messageClass}`}>{message.username}:</span>
          <span className={`message ${messageClass} text-${message.username === username ? "end" : "start"}`}
          style={{ color: textColor }}
          >{message.text}</span>
        </div>
      </MDBListGroupItem>
      );
    }));
  };

  const renderUsers = () => {
    return (members.map((member, index) => {
      return <li key={index}>{member.id}</li>
    }))
  }

  return (
    <div className="container p-4 d-flex-column align-items-center">
      <div className="chat-header d-flex align-items-center justify-content-center">
        <h2 className="my-5 text-black">Moj Chat App</h2>
      </div>
      <div className="chat-body mb-3 p-5 border border-dark border-3">
        <MDBListGroup className="mb-3">{renderMessages()}</MDBListGroup>
        <form onSubmit={handleMessagesSubmit}>
          <div className="text-center">
            <MDBInput className="mb-3 border border-3 border-primary"
              type="text"
              placeholder="Napišite poruku..."
              value={message}
              onChange={handleMessageChange}
              autoFocus={true}
              />
            <MDBBtn color="primary" type="submit">Pošalji</MDBBtn>
          </div>
        </form>
      </div>
      <div className="chat-footer text-center text-black mt-3">
        <p>Vaše trenutno korisničko ime je: <span className="text-white">{username}</span></p>
        <ul>Spojeni korisnici: <span className="text-white">{renderUsers()}</span></ul>
      </div>
    </div>
  );
};
