import React, { useState, useEffect } from 'react';
import { MDBListGroup, MDBListGroupItem, MDBInput, MDBBtn } from 'mdb-react-ui-kit';
import './Chat.css';

export default function App() {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [username, setUsername] = useState("");
  const [userColor, setUserColor] = useState(null);

  // Define a function to calculate the inverse of a hexadecimal color value
  const getInverseColor = (hexColor) => {
    const color = hexColor.slice(1); // Remove the '#' character
    const r = 255 - parseInt(color.slice(0, 2), 16); // Calculate the inverse of the red component
    const g = 255 - parseInt(color.slice(2, 4), 16); // Calculate the inverse of the green component
    const b = 255 - parseInt(color.slice(4, 6), 16); // Calculate the inverse of the blue component
    return `#${r.toString(16).padStart(2, '0')}${g.toString(16).padStart(2, '0')}${b.toString(16).padStart(2, '0')}`; // Combine the components into a single hexadecimal value
  };

  const randomName = () => {
    // ...
  }

  const randomColor = () => {
    return '#' + Math.floor(Math.random() * 0xFFFFFF).toString(16);
  }

  useEffect(() => {
    setUsername(randomName());
    setUserColor(randomColor());
  }, []);

  const CLIENT_ID = "2sxe3EkxMn0uTqkz";
  const drone = new window.Scaledrone(CLIENT_ID, {
    data: {
      name: username
    },
  });

  useEffect(() => {
    // ...
  }, [messages]);

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleMessagesSubmit = (event) => {
    event.preventDefault();

    const newMessage = {
      username: username,
      text: message
    };

    setMessages(messages => [...messages, newMessage]);
    setMessage("");

    drone.publish({
      room: 'observable-room',
      message: newMessage,
      clientData: {
        username: username
      }
    });

    drone.on('error', (error) => {
      alert("Nije uspjelo slanje poruke, molim vas pokuÅ¡ajte ponovo!")
      console.error('Error publishing message:', error);
    });
  };

  const renderMessages = () => {
    return messages.map((message, index) => {
      const messageClass = message.username === username ? 'user-message' : 'other-message';
      const textColor = getInverseColor(userColor); // Calculate the inverse color of the user's color
      return (
        <MDBListGroupItem
          key={index}
          className={`message ${messageClass} text-${message.username === username ? "end" : "start"} mb-2 text-white d-flex justify-content-${message.username === username ? "end" : "start"}`}
          style={{ backgroundColor: message.username === username ? userColor : "", color: textColor }} // Set the background color and text color

      >
        <div className={`d-flex flex-column justify-content-${message.username === username ? "end" : "start"}`}>
          <span className={`font-weight-bold text-black ${messageClass}`}>{message.username}:</span>
          <span className={`message ${messageClass} text-${message.username === username ? "end" : "start"}`}>{message.text}</span>
        </div>
      </MDBListGroupItem>
    );
  });
};

  return (
    <div className="container p-3 d-flex-column align-items-center">
      <div className="chat-header d-flex align-items-center justify-content-center">
        <h2 className="my-5 text-black">Moj Chat App</h2>
      </div>
      <div className="chat-body mb-3 p-5 border border-dark border-3">
        <MDBListGroup className="mb-3">{renderMessages()}</MDBListGroup>
        <form onSubmit={handleMessagesSubmit}>
          <div className="text-center">
            <MDBInput className="mb-3 border border-3 border-primary" type="text" placeholder="NapiÅ¡ite poruku..." value={message} onChange={handleMessageChange} autoFocus={true}/>
            <MDBBtn color="primary" type="submit">PoÅ¡alji</MDBBtn>
          </div>
        </form>
      </div>
      <div className="chat-footer text-center text-black mt-3">
        <p>VaÅ¡e trenutno korisniÄko ime je: <span className="text-white">{username}</span></p>
      </div>
    </div>
  );
};
