import React, { useState, useEffect } from 'react';
import { MDBListGroup, MDBListGroupItem, MDBInput, MDBBtn } from 'mdb-react-ui-kit';
import './Chat.css';

export default function App() {

  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [username, setUsername] = useState("");
  const [drone, setDrone] = useState(null);

  useEffect(() => {

    setDrone(new window.Scaledrone("xZBf1Qt6e8zvkd3X"));

    const randomName = () => {
      const adjectives = ["autumn", "hidden", "bitter", "misty", "silent", "empty", "dry", "dark", "summer", "icy", "delicate", "quiet", "white", "cool", "spring", "winter", "patient", "twilight", "dawn", "crimson", "wispy", "weathered", "blue", "billowing", "broken", "cold", "damp", "falling", "frosty", "green", "long", "late", "lingering", "bold", "little", "morning", "muddy", "old", "red", "rough", "still", "small", "sparkling", "throbbing", "shy", "wandering", "withered", "wild", "black", "young", "holy", "solitary", "fragrant", "aged", "snowy", "proud", "floral", "restless", "divine", "polished", "ancient", "purple", "lively", "nameless"];
      const nouns = ["waterfall", "river", "breeze", "moon", "rain", "wind", "sea", "morning", "snow", "lake", "sunset", "pine", "shadow", "leaf", "dawn", "glitter", "forest", "hill", "cloud", "meadow", "sun", "glade", "bird", "brook", "butterfly", "bush", "dew", "dust", "field", "fire", "flower", "firefly", "feather", "grass", "haze", "mountain", "night", "pond", "darkness", "snowflake", "silence", "sound", "sky", "shape", "surf", "thunder", "violet", "water", "wildflower", "wave", "water", "resonance", "sun", "wood", "dream", "cherry", "tree", "fog", "frost", "voice", "paper", "frog", "smoke", "star"];
      const adjective = adjectives[Math.floor(Math.random() * adjectives.length)];
      const noun = nouns[Math.floor(Math.random() * nouns.length)];
      const user = `${adjective.charAt(0).toUpperCase()}${noun}`
      return user;
      }



    return () => {
        drone.close();
      }
    }, []);

  const handleMessageChange = event => {
    setMessage(event.target.value);
  };

  const handleMessagesSubmit = event => {
    event.preventDefault();

    const newMessage = {
      username: username,
      text: message
    };

    setMessages(messages => [...messages, newMessage]);
    setMessage("");

    drone.publish(
      {
        room: 'observable-room',
        message: newMessage,
        clientData: { 
          username: username
        }
      }
    ).then(() => {
      console.log("Message published successfully!");
    }).catch(error => {
      console.error("Error publishing message:", error);
    });
  };

  const renderMessages = () => {
    return messages.map((message, index) => {
      const messageClass = message.username === username ? 'user-message' : 'other-message';
      return (
        <MDBListGroupItem key={index} className={`d-flex justify-content-${message.username === username ? "end" : "start"}`}>
          <span className={`font-weight-bold ${messageClass}`}>{message.username}</span>: {message.text}
        </MDBListGroupItem>
      );
    });
  };

  return (
    <div className="container">
      <div className="chat-header">
        <h1 className="text-center">Moj Chat App</h1>
      </div>
      <div className="chat-body">
        <MDBListGroup>{renderMessages()}</MDBListGroup>
        <form onSubmit={handleMessagesSubmit}>
          <MDBInput type="text" value={message} onChange={handleMessageChange} placeholder="Napišite poruku"/>
          <MDBBtn color="primary" type="submit">Pošalji</MDBBtn>
        </form>
      </div>
      <div className="chat-footer">
        <p>Vaše trenutno korisničko ime je: {username}</p>
      </div>
    </div>
  );
};
